mapper.select:
SELECT VSCDVY,VSVYVY,VSDRVY,EGNMVR,CNNMVR,LNCDVR FROM NBCT.TCVSVYP,NBCT.TCVSVRP
WHERE VSCDVY=VSCDVR
AND VSDRVY=#{vsdr}

	AND DTEAVY>to_number(to_char(SYSDATE-7,'YYYYMMDD'))


	AND VSVYVY=#{vsvy}


	AND LNCDVR=#{lncd}
	AND DTEAVY>to_number(to_char(add_months(trunc(sysdate),-1),'YYYYMMDD'))


	AND LNCDVR=#{lncd}
	AND VSVYVY=#{vsvy}

AND ROWNUM<=50

ORDER BY DTEAVY DESC

SELECT * FROM ( SELECT VSCD,VSVY,VSDR,CTPF,CTNR,CTCK,CNTRID, CTSZ,CTTY,CTGW,CTSN,LNCD,PTDS, INFE,DECODE(INOG,'Y','Y','N') INOG,DECODE(INTH,'Y','Y','N') INTH,DECODE(INHZ,'Y','Y','N') INHZ,INDM,INTD,ISTRCT, DECODE(ISTRCT,'Y','N',NVL(TRIM(INTS),'N')) INTS, (SELECT wmsys.wm_concat(TRIM(CABLMF)) FROM NBCT.TCBLMFP WHERE VSCDMF=VSCD AND VSVYMF=VSVY AND CTPFMF=CTPF AND CTNRMF=CTNR AND CTCKMF=CTCK) CABL, (SELECT COUNT(CABLMF) FROM NBCT.TCBLMFP WHERE VSCDMF=VSCD AND VSVYMF=VSVY AND CTPFMF=CTPF AND CTNRMF=CTNR AND CTCKMF=CTCK) CABLNUM, (SELECT HZCLHZ FROM NBCT.TCIVHZP WHERE VSCDHZ=VSCD AND VSVYHZ=VSVY AND CTPFHZ=CTPF AND CTNRHZ=CTNR AND CTCKHZ=CTCK AND ROWNUM=1) HZCLHZ, DECODE((SELECT COUNT(*) FROM NBCT.TCEXCCP WHERE VSCDCC=VSCD AND VSVYCC=VSVY AND CTPFCC=CTPF AND CTNRCC=CTNR AND CTCKCC=CTCK),0,'H','R') ISPORTPASS, (SELECT DTTRCC || LPAD(TMTRCC, 6, '0') FROM NBCT.TCEXCCP WHERE VSCDCC=VSCD AND VSVYCC=VSVY AND CTPFCC=CTPF AND CTNRCC=CTNR AND CTCKCC=CTCK AND ROWNUM=1) PASSTIME, DECODE((SELECT COUNT(*) FROM NBCT.TCCTIFP WHERE CNTRIF=CNTRID AND ((IYVSCDIF=VSCD AND IYVSVYIF=VSVY) OR (OYVSCDIF=VSCD AND OYVSVYIF=VSVY)) AND SUBSTR(FLAGIF,5,1)='Y'),0,'N','Y') ISNZWCT, (SELECT TTWTWT-TRWTWT FROM NBCT.TCTRWTP WHERE VSCDWT=VSCD AND VSVYWT=VSVY AND CTPFWT=CTPF AND CTNRWT=CTNR AND CTCKWT=CTCK AND ROWNUM=1) TRWT, (SELECT INTWWT FROM NBCT.TCTRWTP WHERE VSCDWT=VSCD AND VSVYWT=VSVY AND CTPFWT=CTPF AND CTNRWT=CTNR AND CTCKWT=CTCK AND ROWNUM=1) INTW, DECODE((SELECT COUNT(*) FROM NBCT.TCIECKP WHERE VSCDCK=VSCD AND VSVYCK=VSVY AND CTPFCK=CTPF AND CTNRCK=CTNR AND CTCKCK=CTCK AND ROWNUM=1),0,'N','Y') ISCK, (SELECT f_is_trct_jt(VSCD,VSVY,VSDR,CNTRID) ISSRT FROM DUAL) ISSRT, TRIM(SUBSTR(VGMSTR,1,10)) VGMW,TRIM(SUBSTR(VGMSTR,11,10)) VGMM,TRIM(SUBSTR(VGMSTR,21)) VGMS, (SELECT IYDTIF || LPAD(IYTMIF, 6, '0') FROM NBCT.TCCTIFP WHERE IYVSCDIF=VSCD AND IYVSVYIF=VSVY AND CTPFIF=CTPF AND CTNRIF=CTNR AND CTCKIF=CTCK AND ROWNUM=1) INTIME, (SELECT OYDTIF || LPAD(OYTMIF, 6, '0') FROM NBCT.TCCTIFP WHERE OYVSCDIF=VSCD AND OYVSVYIF=VSVY AND CTPFIF=CTPF AND CTNRIF=CTNR AND CTCKIF=CTCK AND ROWNUM=1) OUTTIME FROM ( SELECT VSCDCO VSCD,VSVYCO VSVY,VSDRCO VSDR,CTPFCO CTPF,CTNRCO CTNR,CTCKCO CTCK,CTPFCO||LPAD(CTNRCO,6,'0')||TRIM(CTCKCO) CNTRID, CTSZCO CTSZ,CTTYCO CTTY,CTGWCO CTGW,TRIM(CTSNCO) CTSN,LNCDCO LNCD,TRIM(PTDSCO) PTDS, INFECO INFE,INOGCO INOG,INTHCO INTH,INHZCO INHZ,INDMCO INDM,INTDCO INTD,INTSCO INTS, f_is_trct(VSCDCO,VSVYCO,VSDRCO,CTPFCO||LPAD(CTNRCO,6,'0')||CTCKCO) ISTRCT, (SELECT nbct.f_getVGMInfo(CTPFCO||LPAD(CTNRCO,6,'0')||CTCKCO,VSCDCO,VSVYCO,VSDRCO) FROM DUAL) VGMSTR FROM NBCT.TCMSCOP WHERE VSCDCO=#{vscd} AND VSVYCO=#{vsvy} AND VSDRCO=#{vsdr}
AND LNCDCO IN (SELECT TRIM(SYIDTB) FROM NBCT.SYCDTBP WHERE SYCDTB='TCTBVO' AND (TRIM(SYIDTB)=#{lncd} OR TRIM(SYC2TB)=#{lncd}))
AND CAAGCO=#{caag}
) )
ORDER BY ISPORTPASS,PTDS,CTSZ
ORDER BY PTDS,LNCD,CTSZ
ORDER BY LNCD,CTSZ,CNTRID
ORDER BY CTSZ,LNCD,CNTRID

SELECT DTTR,TMTR,VSCD,VSVY,VSDR,CTPF,CTNR,CTCK,CNTRID,ACCD,JBNR,JBNS,OPCD,OPUSER,USR2, CTSZ,CTTY,CTGW,CTNW,CTSN,INFE,INDM,INHZ,INTH,INOG,INST,INTS,INTD, LNCD,CAAG,PTLD,PTDS,FDCD,CTFR, YDST,YDLN,YDRW,YDEL,VSBA,VSCL,VSEL, DTLD,TMLD,DTDS,TMDS,DTPE,TMPE,DTGI,TMGI,DTRG,TMRG,DTGO,TMGO, TRCD,YDEQ,WTLM,RMKS,IMUSER,IMCTPS,IMDT,IMTM, DECODE((SELECT COUNT(*) FROM NBCT.TCCTIFP WHERE CNTRIF=CNTRID AND ((IYVSCDIF=VSCD AND IYVSVYIF=VSVY) OR (OYVSCDIF=VSCD AND OYVSVYIF=VSVY)) AND SUBSTR(FLAGIF,5,1)='Y'),0,'N','Y') ISNZWCT, DECODE((SELECT COUNT(*) FROM NBCT.TCIECKP WHERE VSCDCK=VSCD AND VSVYCK=VSVY AND CTPFCK=CTPF AND CTNRCK=CTNR AND CTCKCK=CTCK AND ROWNUM=1),0,'N','Y') ISCK, (SELECT f_is_trct_jt(VSCD,VSVY,VSDR,CNTRID) ISSRT FROM DUAL) ISSRT, (SELECT TRIM(SYDSTB) FROM NBCT.SYCDTBP WHERE SYCDTB='TCTBTR' AND SYIDTB=CAAG) CNCAAG, (SELECT TRIM(SYDSTB) FROM NBCT.SYCDTBP WHERE SYCDTB='TCVNPL' AND SYIDTB=CTFR) CNCTFR FROM ( SELECT DTTREM DTTR,LPAD(TMTREM,6,'0') TMTR,TRIM (VSCDEM) VSCD,TRIM (VSVYEM) VSVY,VSDREM VSDR,CTPFEM CTPF,CTNREM CTNR,CTCKEM CTCK,CTPFEM||LPAD(CTNREM,6,'0')||TRIM(CTCKEM) CNTRID, ACCDEM ACCD,JBNREM JBNR,JBNSEM JBNS,OPCDEM OPCD,DECODE(TRIM (USEREM),NULL,'',TRIM (USEREM)) OPUSER,DECODE(TRIM (USR2EM),NULL,'',TRIM (USR2EM)) USR2, CTSZEM CTSZ,CTTYEM CTTY,CTGWEM CTGW,CTNWEM CTNW,TRIM (CTSNEM) CTSN, INFEEM INFE,INDMEM INDM,INHZEM INHZ,INTHEM INTH,INOGEM INOG,INSTEM INST,INTSEM INTS,INTDEM INTD, LNCDEM LNCD,CAAGEM CAAG, PTLDEM PTLD,PTDSEM PTDS,FDCDEM FDCD,CTFREM CTFR, YDSTEM YDST,YDLNEM YDLN,YDRWEM YDRW,YDELEM YDEL,VSBAEM VSBA,VSCLEM VSCL,VSELEM VSEL, DTLDEM DTLD,DECODE(DTLDEM,0,0,LPAD(TMLDEM,6,'0')) TMLD,DTDSEM DTDS,DECODE(DTDSEM,0,0,LPAD(TMDSEM,6,'0')) TMDS, DTPEEM DTPE,DECODE(DTPEEM,0,0,LPAD(TMPEEM,6,'0')) TMPE,DTGIEM DTGI,DECODE(DTGIEM,0,0,LPAD(TMGIEM,6,'0')) TMGI, DTRGEM DTRG,DECODE(DTRGEM,0,0,LPAD(TMRGEM,6,'0')) TMRG,DTGOEM DTGO,DECODE(DTGOEM,0,0,LPAD(TMGOEM,6,'0')) TMGO, TRCDEM TRCD,YDEQEM YDEQ,WTLMEM WTLM, RMKSEM RMKS, USERIM IMUSER,TRIM (CTPSIM) IMCTPS,DTTRIM IMDT,DECODE(DTTRIM,0,0,LPAD(TMTRIM,6,'0')) IMTM FROM NBCT.TCDLEMP,NBCT.CUSTIMP
WHERE ACCDEM=#{accd} AND JBNREM=#{jbnr} AND JBNSEM=#{jbns}
WHERE CTPFEM=#{ctpf} AND CTNREM=#{ctnr} AND CTCKEM=#{ctck}
AND VSCDEM=VSCDIM(+) AND VSVYEM=VSVYIM(+) AND VSDREM=VSDRIM(+) AND CTPFEM=CTPFIM(+) AND CTNREM=CTNRIM(+) AND CTCKEM=CTCKIM(+) ) ORDER BY DTTR DESC,TMTR DESC
